id like '%${buySearch}%' or name like '%${buySearch}%' or tel like '%${buySearch}%'
id name tel like '%${buySearch}%'
) A, ( select @NO := 0 ) B ) C WHERE C.ROWNUM BETWEEN #{start} AND #{end}

mapper.update:
update buy
set buy_situation = #{buy_situation}
where buy_id = #{buy_id}

